   496â†’        vsla_gpu_add_kernel_f32<<<grid_size, block_size, 0, (cudaStream_t)result->stream>>>(
   497â†’            (vsla_gpu_f32_t*)result->gpu_data, (const vsla_gpu_f32_t*)a->gpu_data, 
