    61â†’    
    62â†’    // Hardware info
    63â†’    bool cuda_available;
    64â†’    bool rocm_available;
    65â†’    bool oneapi_available;
    66â†’    int gpu_device_id;
    67â†’    size_t gpu_memory_total;
