   172â†’void vsla_cleanup(vsla_context_t* ctx) {
   173â†’    if (!ctx) return;
   174â†’    
   175â†’#ifdef VSLA_ENABLE_CUDA
   176â†’    if (ctx->gpu_ctx) {
   177â†’        vsla_gpu_destroy(ctx->gpu_ctx);
   178â†’    }
   179â†’#endif
   180â†’    
   181â†’    free(ctx->fft_backends);
   182â†’    free(ctx);
   183â†’}
